INSERT INTO `book`(`id`, `avatar`, `name`, `description`, `publish_date`, `author`, `publisher`, `category`, `book_no`, `createtime`, `updatetime`, `score`, `nums`) VALUES (4, 'https://bookcover.yuewen.com/qdbimg/349573/1021617576/180', '夜的命名术', '蓝与紫的霓虹中，浓密的钢铁苍穹下，数据洪流的前端，是科技革命之后的世界，也是现实与虚幻的分界。\n　　钢铁与身体，过去与未来。\n　　这里，表世界与里世界并存，面前的一切，像是时间之墙近在眼前。\n　　黑暗逐渐笼罩。\n　　可你要明白啊我的朋友，我们不能用温柔去应对黑暗，要用火。', '2021-11-24', '会说话的肘子', '起点中文', '都市 > 异术超能', 'qd-000001', '2022-11-12 20:03:38', NULL, 10, 49);
INSERT INTO `book`(`id`, `avatar`, `name`, `description`, `publish_date`, `author`, `publisher`, `category`, `book_no`, `createtime`, `updatetime`, `score`, `nums`) VALUES (5, 'https://bookcover.yuewen.com/qdbimg/349573/1031940621/180', '灵境行者', '亘古通今，传闻世有灵境。\n关于灵境的说法，历朝历代的名人雅士众说纷纭，诗中记载：\n“自齐至唐，兹山濅荒，灵境寂寥，罕有人游。”\n“灵境不可状，鬼工谅难求。”', '2022-09-13', '卖报小郎君', '中文网', '科幻 > 进化异变', 'qd-000002', '2022-11-12 20:08:12', NULL, 15, 29);
INSERT INTO `book`(`id`, `avatar`, `name`, `description`, `publish_date`, `author`, `publisher`, `category`, `book_no`, `createtime`, `updatetime`, `score`, `nums`) VALUES (6, 'https://bookcover.yuewen.com/qdbimg/349573/1031777108/180', '\r\n光阴之外', '天地是万物众生的客舍，光阴是古往今来的过客。\r\n　　死生的差异，就好像梦与醒的不同，纷纭变换，不可究诘。\r\n　　那么超越了生死，超脱了天地，在光阴之外，等待我们的是什么？', '2019-2-2', '耳根', '中文网', '科幻 > 进化异变', 'qd-000003', '2022-11-12 20:09:02', NULL, 10, 8);
